import { areaRadial } from "d3"

const styles = { 
    bar : {
        transform: 'translate(2.5%, 0) rotate(180deg)'
    },
    barContainer1 : {
        transform: 'translate(47%, 90%)'
    },
    barContainer2 : {
        transform: 'translate(53%, 90%)'
    },
    text : {
        fontFamily: 'Arial',
        fontSize: '1em'
    }

}

export default styles
