import React from 'react';
import Simulation from '../Simulation'

export default class App extends React.PureComponent {
    constructor(props) {
        super(props)

        this.state = {
            startSim : false,
        }
    }


startSimulation = () => {
    this.setState({ startSimulation : true});
};

render() {

    const { startSim } = this.state;

    return(
        <div>
            <button onPress={this.startSimulation}>Start</button>
            {startSim && <Simulation />}
        </div>
    )
}
}
