import { areaRadial } from "d3"

const styles = { 
    bar : {
        transform: 'translate(2.5%, 0) rotate(180deg)'
    },
    barContainer1 : {
        transform: 'translate(45%, 90%)'
    },
    barContainer2 : {
        transform: 'translate(55%, 90%)'
    },
    text : {
        fontFamily: 'Arial',
        fontSize: '2em',
        textAnchor: 'middle',
        fill: 'white'
    }

}

export default styles
