import React from 'react';
import * as d3 from 'd3';
import styles from './style';

export default class Bars extends React.PureComponent {
  constructor(props) {
    super(props);
    this.references = { stats: React.createRef() };
  }

  componentDidMount() {
    this.animate();
  }

  componentDidUpdate() {
    this.animate();
  }

  animate() {
    switch (this.props.phase) {
      case 2:
        this.animatePhase2();
        break;
      default:
        this.animatePhase1();
    }
  }

  animatePhase2() {
    const { percentage } = this.props;
  }

  render() {

    return (
        <g>
            <rect ref={this.references.stats} style={styles.bar}
                x='45%'
                y='90%'
                width='5%'
                height='10'
                fill='#2396CD'
                strokeWidth='0'
            />
            <rect ref={this.references.stats} style={styles.bar}
                x='55%'
                y='90%'
                width='5%'
                height='10'
                fill='#2396CD'
                strokeWidth='0'
            />
        </g>
    );
  }
}
