import React from 'react';
import * as d3 from 'd3';
import styles from "./style.js";

export default class Titles extends React.PureComponent {
  constructor(props) {
    super(props);
    this.references = { title: React.createRef() };
  }

  componentDidMount() {
    this.animate();
  }

  animate() {
    switch (this.props.phase) {
      case 1:
        this.animatePhase1();
        break;
      case 2:
        this.animatePhase2();
        break;
      default:
        this.animatePhase1();
    }
  }

  animatePhase1() {
    d3.select(this.references.title.current)
      .style('opacity', 0)

  }

  animatePhase2() {
    d3.select(this.references.title.current)
    .transition()
    .duration(1000)
    .style('opacity', 1.0)
  }


  render() {
    return (
      <g>
      <text ref={this.references.title}
        x= "25%"
        y= "10%"
        width="50%"
        height="10%"
        style={styles.text}
        >
          Zwarte mensen
        </text>

      <text ref={this.references.title}
        x= "75%"
        y= "10%"
        width="50%"
        height="10%"
        style={styles.text}
        >
          Witte mensen
        </text>
      </g>
    );
  }
}
